                <th>Description</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for categorie_rec in categorie_recs %}
            <tr>
                <td>{{ categorie_rec.id }}</td>
                <td>{{ categorie_rec.nom }}</td>
                <td>{{ categorie_rec.description }}</td>
                <td>
                    <a href=\"{{ path('categorie_rec_show', {'id': categorie_rec.id}) }}\">show</a>
                    <a href=\"{{ path('categorie_rec_edit', {'id': categorie_rec.id}) }}\" class=\"btn btn-secondary btn-circle btn-group-lg\">
